import {getallpokemons} from '../actions/actions'
import {useDispatch, useSelector} from 'react-redux'
import Cargando from './cargando'
import Cards from './cards'

function Allpokemons(){
    const dispatch = useDispatch()
    let pokemons = useSelector(state => state.pokemons)
    let items_pagina=12;
    let aux=[...pokemons].splice(0, items_pagina)
    const [items,setitems]=useState([...pokemons].splice(0, items_pagina))
    const [current,setcurrent]=useState(0)
    
    console.log(items)
   
      useEffect(() =>{
          dispatch(getallpokemons())
         
        }
          ,[pokemons,items, current])


          const prev=()=>{
            const prev = current -1;
            if (prev<0) return
            const first=prev * items_pagina
            setitems([...pokemons].splice(first, items_pagina))
           setcurrent(prev)

            console.log('prev')
          }
          const next=()=>{
           const max=pokemons.length
           const next = current+1
           const first=current * items_pagina
         
           setitems([...pokemons].splice(first, items_pagina))
           setcurrent(next)
           aux=items;
            console.log('next')
          }

        
        if(!pokemons.length){
          return(
          <Cargando></Cargando>
        )}    
     else {
       
       return(
       <div>
         <button onClick={prev} >prev</button>
         <button onClick={next}>next</button>
         <Cards pokemons={aux}  ></Cards>
       </div>
     )}
}

export default Allpokemons;
