 function Pokemon() {
    const dispatch = useDispatch;
    const {id} = useParams()
    console.log(id)
    useEffect(() =>{
      dispatch(getpokemon(id))
     
    },[dispatch])
    

    
      return (<div>
          <h1> estamos en el id : {id} </h1>
      </div>)
  
}

export default Pokemon
