import React from "react";
import './cards.css';
import {Link} from 'react-router-dom';
// import {getallpokemons} from '../actions/actions'
// import {useDispatch, useSelector} from 'react-redux'



function Cards ({pokemons}){
     console.log(pokemons.length)

     const redirect = (id)=>{
      window.location = `http://localhost:3000/pokemons/${id}`;
     }

  
  
          return(<div className="container">
    {pokemons && 
    pokemons.map(r=>{ if(typeof r.id=== 'string'){
      return (
        <div className='card'> 
        <img src={r.img} width="80" height="80" alt=""/>
        <Link to={`/pokemons/${r.id} `}>
        <p>{r.name}</p>
        </Link>
        <p>{r.id}</p>
        {r.types.map(t=>{
          return (<p>{t}</p>)
        })}
        <input type="submit" onClick={redirect(r.id)}>Details</input>
        </div>
     )
    }
      
      return (
        <div className='card'> 
        <img src={r.img} width="80" height="80" alt=""/>
        <Link to={`/pokemons/${r.id} `}>
        <p>{r.name}</p>
        </Link>
        <p>{r.id}</p>
        <p>{r.type}</p>
        {/* <input type="submit" onClick={redirect(r.id)}>Details</input> */}
        <input type='submit' value='Buscar' onClick={redirect(r.id)} />
        </div>
     )

      
      
    
    })
  }
</div>)
}

export default Cards
