import React, {useEffect,useState} from "react";
import {useParams,NavLink} from "react-router-dom";
import {useDispatch,useSelector} from 'react-redux'
import {search} from '../actions/actions'



function Search (){
    const dispatch = useDispatch()
    const {name} = useParams()
    useEffect(() =>
    dispatch(search(name))
     ,[dispatch])
     const pokemon=useSelector(state => state.search)

     if(pokemon.name){
        return (<div className="detail">
          <NavLink to='/pokemons'>Volver</NavLink>
                 <h1>{pokemon.name} </h1>
                 <img src={pokemon.img} width="250" height="250" alt=""/>
                 <p>Type:{pokemon.type}</p>
                 <p>HP:{pokemon.hp}</p>
                 <p>Attack:{pokemon.strength}</p>
                 <p>Defense:{pokemon.defending}</p>
                 <p>Speed:{pokemon.velocity}</p>
                 <p>Height:{pokemon.height}</p>
                 <p>Weight:{pokemon.weight}</p>
              
             </div>)
       } else {return(
        <div className="detail">
          <NavLink to='/pokemons'>Volver</NavLink>
           <h1>El ID no corresponde a un Pokemon disponible</h1>
          </div>
        )}
}

export default Search
