import React, {useEffect,useState} from "react";
import {getallpokemons} from '../actions/actions'
import {useDispatch, useSelector} from 'react-redux'
import Cargando from './cargando'
import Cards from './cards'
import './allpokemons.css'

 function Allpokemons(){
    const dispatch = useDispatch()
    let pokemons =  useSelector(state => state.pokemons)
    let [poke,setpoke]=useState(pokemons)
    let [filtrados,setfiltrados]=useState(pokemons)
    const [current,setcurrent]=useState(0)
    const [search,setsearch]=useState('')
      useEffect(() =>{
          dispatch(getallpokemons())
         
        },[dispatch])
        useEffect(() =>{
          items()
        },[poke,filtrados])
        let items = ()=>{
          if(filtrados.length){
            const busqueda = pokemons.filter(item => item.name.includes(search))
            return busqueda.slice(current, current + 12)}
          if(poke.length){
            const busqueda = pokemons.filter(item => item.name.includes(search))
          return busqueda.slice(current, current + 12)
          }
          if(!search.length){
          return pokemons.slice(current, current + 12)}
          else {
          const busqueda = pokemons.filter(item => item.name.includes(search))
          return busqueda.slice(current, current + 12)}

        }
        const next=()=>{
          if (pokemons.length>current+12){
          setcurrent(current + 12)}}
        
        const prev=()=>{
          if (!current<=0){
          setcurrent(current - 12)}
        }
        const onsearch = ({target})=> {
          setcurrent(0)
          setsearch(target.value)
        }
        const orderby = async(e)=>{
           console.log(e.target.value)
          switch(e.target.value){
           case 'id decreciente': setpoke(...pokemons.sort((a,b)=>( b.id)-(a.id)));
                  break;
           case 'id creciente':setpoke(...pokemons.sort((a,b)=>( a.id)-(b.id)));
           break;
           case 'HP decreciente':setpoke(...pokemons.sort((a,b)=>( b.hp)-(a.hp)));
           break;
           case 'HP creciente':setpoke(...pokemons.sort((a,b)=>( a.hp)-(b.hp)));
           default: setpoke(pokemons)

          }}
        const filterbytype = async(e)=>{
          console.log(e.target.value)
          const filtrar = await pokemons.filter(b=> b.type.includes(e.target.value))
          console.log(filtrar)
          setfiltrados(filtrar)
          
          // switch(e.target.value){
          //   case 'water': setpoke(...pokemons.filter(b=> b.type === e.target.value));
          //          break;
          //   case 'fire':setpoke(...pokemons.filter(b=> b.type === e.target.value));
          //   break;
          //   case 'grass':setpoke(...pokemons.filter(b=> b.type === e.target.value));
          //   break;
          //   case 'normal':setpoke(...pokemons.filter(b=> b.type === e.target.value));
          //   break
          //   case 'poison':setpoke(...pokemons.filter(b=> b.type === e.target.value));
          //   break
          //   case 'bug':setpoke(...pokemons.filter(b=> b.type === e.target.value));
          //   default: setpoke(pokemons)
        
        }

        if(!pokemons.length){
          return(
          <Cargando/>
        )}    
     else if (items().length){return(
       <div className='container'>
         <button onClick={prev} >prev</button>
         <button onClick={next}>next</button>

          Metodos de ordenamiento:{
            <div>
            <input type='submit' value='id decreciente' onClick={orderby} />
            <input type='submit' value='id creciente' onClick={orderby} />
            <input type='submit' value='HP decreciente' onClick={orderby} />
            <input type='submit' value='HP creciente' onClick={orderby} />
          </div>
          }
         <input type="text" placeholder="Search..." value={search} onChange={onsearch}/>
         Mostrar los tipo:{
           <div>
             <input type='radio' id='water' value='water' onClick={filterbytype} /><label for='water'>water</label>
             <input type='radio' id='fire' value='fire' onClick={filterbytype} /><label for='fire'>fire</label>
             <input type='radio' id='normal' value='normal' onClick={filterbytype} /><label for='normal'>water</label>
             <input type='radio' id='poison' value='poison' onClick={filterbytype} /><label for='poison'>poison</label>
             <input type='radio' id='grass' value='grass' onClick={filterbytype} /><label for='grass'>grass</label>
             <input type='radio' id='bug' value='bug' onClick={filterbytype} /><label for='bug'>bug</label>
             
           </div>
         }
         <Cards pokemons={items()}  ></Cards>
       </div>
     )}
     else{return(
       <div> 
         <input type="submit" onclick={setsearch('')}>volver</input>
       </div>
     )}
}

export default Allpokemons;
