import React, {useEffect} from "react";
import {getpokemon,getallpokemons} from '../actions/actions'
import {useParams} from "react-router-dom";
import {useDispatch,useSelector} from 'react-redux'
import './detail.css';


 function Pokemon(){
    const dispatch = useDispatch();
    const {id} = useParams()
  if(typeof id!== 'number'){
    useEffect(() =>{
      dispatch(getallpokemons())
    },{dispatch})
    let pokemons =  useSelector(state => state.pokemons) 
  pokemons.map(p=>{
  if(p.id===id){
    return (<div className="detail">
          <h1>{p.name} </h1>
          <img src={p.img} width="250" height="250" alt=""/>
          <p>Type:{p.types[0].name}</p>
          <p>HP:{p.hp}</p>
          <p>Attack:{p.strength}</p>
          <p>Defense:{p.defending}</p>
          <p>Speed:{p.velocity}</p>
          <p>Height:{p.height}</p>
          <p>Weight:{p.weight}</p>
        
      </div>)}else {
        return(
          <div className="detail">
            <h1>El ID no corresponde a un Pokemon disponible</h1>
          </div>
        )
      }
  

  })
    
  




  }


    useEffect(() =>{
      dispatch(getpokemon(id))
    },{dispatch})
    let pokemon = useSelector(state => state.pokemon)
    
    if(id<=40){
      return (<div className="detail">
          <h1>{pokemon.name} </h1>
          <img src={pokemon.img} width="250" height="250" alt=""/>
          <p>Type:{pokemon.type}</p>
          <p>HP:{pokemon.hp}</p>
          <p>Attack:{pokemon.strength}</p>
          <p>Defense:{pokemon.defending}</p>
          <p>Speed:{pokemon.velocity}</p>
          <p>Height:{pokemon.height}</p>
          <p>Weight:{pokemon.weight}</p>
        
      </div>)}else {
        return(
          <div className="detail">
            <h1>El ID no corresponde a un Pokemon disponible</h1>
          </div>
        )
      }
  
}

export default Pokemon
