const inicialState = {
    pokemons: [],
    pokemon:{},
    types: [],
    search:{}
}


function reducer (state=inicialState,{type,payload}) {
    switch(type){
         case 'GET_POKEMONS': {
         let arr = payload
         return {...state,pokemons:arr }}
        
         case 'GET_POKEMON':{
             return {...state,pokemon:payload}
         }
        case 'GET_TYPE':{
           return {...state,types:payload}
        }
        case 'GET_SEARCH':{
            return {...state,search:payload}
        }
        case 'GET_SORT':{
            let ordenado 
            switch(payload){
                case 'id decreciente':ordenado= state.pokemons.sort((a,b)=> b.id<a.id?1 : -1);
                      
                case 'id creciente':ordenado= state.pokemons.sort((a,b)=> a.id<b.id?1 : -1);
                
                case 'attack decreciente':ordenado= state.pokemons.sort((a,b)=> b.strength<a.strength?1 : -1);
                
                case 'attack creciente':ordenado= state.pokemons.sort((a,b)=> a.strength<b.strength?1 : -1);
                
                case 'orden alfabetico': ordenado= state.pokemons.sort((a, b) => a.name > b.name ? 1 : -1);
                
                }
                console.log(ordenado);
            return {...state,
                pokemons:ordenado
                }
        }
         



        default: return state
      }
}


export default reducer
