import React ,{useEffect,useState} from "react";
import {createpokemon,gettype} from "../actions/actions"
import {useDispatch, useSelector} from 'react-redux'
import './create.css';


function Createpokemons(){
    const dispatch = useDispatch()
    useEffect(() =>{
        dispatch(gettype())
      },[])
      const types = useSelector(state => state.types)
      console.log(types)
    const [input,setInput] = useState({
            name:'',
            Health:0,
            strength:0,
            defending:0,
            velocity:0,
            height:0,
            weight:0,
            types
    })
    function onChange(e) {
        console.log(e)
        setInput({
            ...input,
            [e.target.name]:e.target.value
        })
    }
    function handleSubmit(){
        createpokemon(input)
    }
    function handletype(e){
        
        setInput({
            ...input,
            types:e.target.value
        })
    }

    return(
        <div className='create'>
            <form>
        <label htmlFor="name">Name:</label>
        
        <input type="text" name='name' onChange={onChange}/>
        <label htmlFor="Health">Health:</label>
        <input type="number" name='Health'onChange={onChange}/>
        <label htmlFor="strength">Strength:</label>
        <input type="number" name='strength'onChange={onChange}/>
        <label htmlFor="defending">Defending:</label>
        <input type="number" name='defending'onChange={onChange}/>
        <label htmlFor="velocity">Velocity:</label>
        <input type="number" name='velocity'onChange={onChange}/>
        <label htmlFor="height">Height:</label>
        <input type="number" name='height' onChange={onChange}/>
        <label htmlFor="weight">Weight:</label>
        <input type="number" name='weight' onChange={onChange}/>

        <select type="submit" name="typos" value='1' onChange={handletype}>
            <option value='0' >seleccionar type</option>
               {types.map((type) => <option value={type.name}>{type.name} </option>
                            )}
             </select>

        <input type="submit" onClick={handleSubmit} />
        </form>
        </div>
    )
}


export default Createpokemons
