import React, {useEffect,useState} from "react";
import {getallpokemons,gettype} from '../actions/actions'
import {useDispatch, useSelector} from 'react-redux'
import Cargando from './cargando'
import Cards from './cards'
import './allpokemons.css'
import {NavLink} from 'react-router-dom';

  function Allpokemons(){
    const dispatch = useDispatch()
    let pokemons =  useSelector(state => state.pokemons)
    
    let [poke,setpoke]=useState([])
    let [filtrados,setfiltrados]=useState(pokemons)
    const [current,setcurrent]=useState(0)
    const [search,setsearch]=useState('')
      useEffect(() =>{
          dispatch(getallpokemons())
          dispatch(gettype())
          
          
         
        },[dispatch])
        // useEffect(() =>setfiltrados(pokemons))
        let types=  useSelector(state => state.types)
        let items = ()=>{
          if(filtrados.length){
            console.log('filtrados')
            return filtrados.slice(current, current + 12)}
          if(poke.length){
            
          return poke.slice(current, current + 12)
          }
          else {
          return pokemons.slice(current, current + 12)}

        }
        const next=()=>{
          if(filtrados.length>current + 12){
            console.log('next filtrados')
            setcurrent(current + 12)
          }else{return}
        }
        const prev=()=>{
          if (!current<=0){
          setcurrent(current - 12)}
        }
        const onsearch = ({target})=> {

          setsearch(target.value)
        }
        const redirect = ()=>{
          console.log('redirect')
            window.location = `http://localhost:3000/pokemons/search/${search}`;
         
        }
        const orderby = async(e)=>{
           console.log(e.target.value)
          switch(e.target.value){
           case 'id decreciente': setpoke(...pokemons.sort((a,b)=>( b.id)-(a.id)));
                  break;
           case 'id creciente':setpoke(...pokemons.sort((a,b)=>( a.id)-(b.id)));
           break;
           case 'attack decreciente':setpoke(...pokemons.sort((a,b)=>( b.strength)-(a.strength)));
           break;
           case 'attack creciente':setpoke(...pokemons.sort((a,b)=>( a.strength)-(b.strength)));
           break;
           case 'orden alfabetico':setpoke(...pokemons.sort((a,b)=>( b.name)-(a.name)));
           break;
           default: setpoke(pokemons)

          }}
        const filterbytype =async (e)=>{
          const valor=e.target.value
           if(valor==='todos') return setfiltrados(pokemons)
           let filtrados =[]
             await pokemons.map(e=>{
              if(typeof e.id!== 'number'){
                console.log(e)
                e.types.map(t=>{if(t.name===valor){ filtrados.push(e)}})
                // if(e.types[0].name===valor){ filtrados.push(e)}
              }else{
                if(e.type===valor){ filtrados.push(e)}
              }
              
              })
              console.log(filtrados)
              setfiltrados(filtrados)
        }

        if(!pokemons.length){
          return(
          <Cargando/>
        )}    
     else if (items().length){return(
       <div className='container'>
         <div className='filtros'>
         <button className='boton' onClick={prev} ><span>Prev</span></button>
         <button className="boton" onClick={next}><span>Next</span></button>
            
            <input  className='input' type='submit' value='id decreciente' onClick={orderby} />
            <input  className='input' type='submit' value='id creciente' onClick={orderby} />
            <input  className='input' type='submit' value='attack decreciente' onClick={orderby} />
            <input   className='input'type='submit' value='attack creciente' onClick={orderby} />
            <input   className='input'type='submit' value='orden alfabetico' onClick={orderby} />
          
          
              
          
         <input  className='buscar'type="text" placeholder="Search..." value={search} onChange={onsearch}/><input className='input' type='submit' value='Buscar' onClick={redirect} />
  
        
          
             <select className='input' name="typos" id="" onChange={filterbytype}>
               <option value='todos'>Todos</option>
               {types.map((type) => (
                                <option value={type.name}>
                                    {type.name}
                                </option>
                            ))}
             </select>
             
           
           <NavLink  className='link' to={'/create'}>Create</NavLink>
           </div>
         <Cards pokemons={items()}  ></Cards>
         
       </div>
     )}
     
}

export default Allpokemons;
