    //   </div>)
    // }
  
  
    






  
  if(typeof id!== 'number'){
    
    console.log('aca entra')
    let index1=null;
  pokemons.map((p,index)=>{
    console.log('en el map')
    console.log(p.id)
  if(p.id===id){
   index1=index
  }
    console.log('aca tambien entra')
    return (<div className="detail">
          <h1>{pokemons[index1].name} </h1>
          <img src={pokemons[index1].img} width="250" height="250" alt=""/>
          <p>Type:{pokemons[index1].types[0].name}</p>
          <p>HP:{pokemons[index1].hp}</p>
          <p>Attack:{pokemons[index1].strength}</p>
          <p>Defense:{pokemons[index1].defending}</p>
          <p>Speed:{pokemons[index1].velocity}</p>
          <p>Height:{pokemons[index1].height}</p>
          <p>Weight:{pokemons[index1].weight}</p>
        
      </div>)
  

  })
  }


 
    if(id<=40){
      return (<div className="detail">
          <h1>{pokemon.name} </h1>
          <img src={pokemon.img} width="250" height="250" alt=""/>
          <p>Type:{pokemon.type}</p>
          <p>HP:{pokemon.hp}</p>
          <p>Attack:{pokemon.strength}</p>
          <p>Defense:{pokemon.defending}</p>
          <p>Speed:{pokemon.velocity}</p>
          <p>Height:{pokemon.height}</p>
          <p>Weight:{pokemon.weight}</p>
        
      </div>)}
      else {
        console.log("ole")
        return(
          <div className="detail">
            <h1>El ID no corresponde a un Pokemon disponible</h1>
          </div>
        )
      }
  
}

export default Pokemon
